import { useState } from 'react';
import './ExpensesFilter.css';

const ExpensesFilter = (props) => {

    //const [selectedYear, setSelectedYear] = useState('')

    const selectYearHandler = (event) => {
        let SelectedYear = event.target.value;
        props.onSelectYear(selectedYear);
    };

    return (
        <div className='expenses-filter'>
            <div className='expenses-filter__control'>
                <label>Filter by year</label>
                <select onChange={selectYearHandler}>
                    <option value='2022' onSelect={selectYearHandler}>2022</option>
                    <option value='2021' onSelect={selectYearHandler}>2021</option>
                    <option value='2020' onSelect={selectYearHandler}>2020</option>
                    <option value='2019' onSelect={selectYearHandler}>2019</option>
                </select>
            </div>
        </div>
    );
};

export default ExpensesFilter;
