import './Expenses.css'
import ExpenseItem from "../ExpenseItem/ExpenseItem";
import Card from '../../UI/Card/Card';
import ExpensesFilter from '../ExpensesFilter/ExpensesFilter';
import { useState } from 'react';

const Expenses = (props) => {
    const [selectedYear, setSelectedYear] = useState('2022');
    const selectYearHandler = (year) => {
        setSelectedYear(year);
    };

    const filteredExpenses = props.expenses.filter((expense => expense.date.getFullYear() == selectedYear));

    let expensesContent = <p className='no-expenses-message'>No expenses found</p>;
    if (filteredExpenses.length > 0) {
        expensesContent = filteredExpenses.map((expense) => {
            return (
                <ExpenseItem
                    key={expense.id}
                    title={expense.title}
                    date={expense.date}
                    amount={expense.amount} />
            );
        });
    }
    return (
        <Card className='expenses'>
            <ExpensesFilter selected={selectedYear} onSelectYear={selectYearHandler} />
            {expensesContent}
        </Card>
    );
}
export default Expenses;
