import { useState } from 'react';
import './ExpensesFilter.css';

const ExpensesFilter = (props) => {

    const [selectedYear, setSelectedYear] = useState('')

    const selectYearHandler = (event) => {
        setSelectedYear(event.target.value);
        props.selectYear(selectedYear);
    };

    return (
        <div className='expenses-filter'>
            <div className='expenses-filter__control'>
                <label>Filter by year</label>
                <select>
                    <option value='2022' onClick={selectYearHandler}>2022</option>
                    <option value='2021' onClick={selectYearHandler}>2021</option>
                    <option value='2020' onClick={selectYearHandler}>2020</option>
                    <option value='2019' onClick={selectYearHandler}>2019</option>
                </select>
            </div>
        </div>
    );
};

export default ExpensesFilter;
